import React from 'react';

// imported from material UI DOCS
import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Link from '@material-ui/core/Link';
import AdditionProblem from './AdditionProblem'
// END MATERIAL UI IMPORTS

export default class AdditionCard extends React.Component {

    state= {
        sum: 0,
        userInput: 0
    }

    // newSum = () => {
    //     this.setState({
    //         sum: sum
    //     })
    // }

    getSum = (sum) => {
        this.setState({sum: sum})
    }




     render(){

        return (


            <Container className="ContainerclassName" maxWidth="md">
            <Grid container spacing={4}>

                <Grid item key="1" xs={12} sm={6} md={4}>
                <Card className="cardclassName">
                    <CardMedia
                    className="cardMediaclassname"
                    image="https://source.unsplash.com/random"
                    title="Image title"
                    />
                    <CardContent className="cardContentclassName">
                    <Typography gutterBottom variant="h5" component="h2">
                        <AdditionProblem sum={this.getSum}/>
                    </Typography>
                    </CardContent>
                    <CardActions>
                    <Button size="small" color="primary">
                        View
                    </Button>
                    <Button size="small" color="primary">

                    </Button>
                    </CardActions>
                </Card>
                </Grid>

            </Grid>
            </Container>

    );
}
}
