import React from 'react';

// imported from material UI DOCS
// import AppBar from '@material-ui/core/AppBar';
// import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
// import CardMedia from '@material-ui/core/CardMedia';
// import CssBaseline from '@material-ui/core/CssBaseline';
// import Grid from '@material-ui/core/Grid';
// import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
// import { makeStyles } from '@material-ui/core/styles';
// import Container from '@material-ui/core/Container';
// import Link from '@material-ui/core/Link';

const DEFAULT_STATE= {
    total: 0,
    userInput: 0,
    a: 0,
    b: 0,
    point: 2
}


export default class MultiplicationCard extends React.Component {

   state ={...DEFAULT_STATE}

    // newSum = () => {
    //     
    // }


    // getSum = (sum) => {
    //     this.setState({
    //         sum: sum
    //     })
    // }
    mathProblem = () => {
    let random = () => {   
        return Math.round(Math.random()* 10)+5 
    }

    let a = random()
    let b = random()
    let total = a * b

    this.setState({
        total: total,
        a: a,
        b: b
    })
    }
    componentDidMount(){
        // console.log('hey')
        this.mathProblem()

    }

    // componentDidUpdate(){

    // }



    // componentWillMount(){
    //     this.setState({
    //         sum: newSum
    //         })


    // }
    handleChange = (event) => {
        this.setState({
            userInput: event.target.value
        })
        console.log(this.state.userInput)
    }

    keyPress = (event) => {
        if (event.key === "Enter" && this.state.userInput == this.state.total){
            this.mathProblem()
            this.props.correct(this.state.point)
            event.target.value = ""

        }
        else if(event.key === "Enter"){
            console.log(typeof this.state.userInput)
        }
    }



     render(){

        return (



                <Card className="cardclassName">
                    <CardContent className="cardContentclassName">
                    <Typography gutterBottom variant="h5" component="h2">
                    <h1>{this.state.a} * {this.state.b}</h1>
                    </Typography>
                    </CardContent>
                    <CardContent></CardContent>
                    <CardActions>
                        <Typography>2 Points</Typography>
                        <Typography>
                        <input 
                            type="text"
                            onChange= {this.handleChange}
                            onKeyPress= {this.keyPress}
                            />
                            </Typography>

                    </CardActions>
                </Card>


    );
}
}
