    mathProblem = () => {
    let random = () => {   
        return Math.round(Math.random()* 100)
    }

    let a = random()
    let b = random()
    let difference
    if(a >= b){
        return difference = a - b
    } else if(a < b){
        return difference = b - a
    }


    this.setState({
        difference: difference,
        a: a,
        b: b
    })
