import React, { Component } from 'react'

const DEFAULT_STATE = {
    counter: 60
}

export default class Timer extends Component {

    state = {...DEFAULT_STATE}



    countDown = () => {
        this.state.counter--
    }

    changeTime = () => {
        this.setState({counter: this.countDown()})
    }

    timer = () => {
        setInterval(this.changeTime, 1000);

    }


    render() {
        return (
            <div>
               <h1>
                    Timer: {this.state.counter}
                    {this.timer()}
               </h1> 
            </div>
        )
    }
}






