import React, { Component } from 'react'

export default class Timer extends Component {

    state = {
        counter: 60
    }



    timer = () => {
        setInterval(function() {
            this.setState({counter: --this.state.counter})


        }, 1000);

    }


    render() {
        return (
            <div>
               <h1>
                    Timer: {this.state.counter}
                    {this.timer()}
               </h1> 
            </div>
        )
    }
}






