        .then(resp => resp.json())
        .then(scores => {
           scores.map(score )
           this.setState({scores: scores})
        })

    }


    render() {

        return (
            <div>
                <Grid>
                    {this.state.scores}
                </Grid>
            </div>
        );
    }
}

